import {useState} from 'react'
function App() {
  const [counter, setCounter] = useState(1)
  const handleIncrease = () => {
    // ra cùng result
    // setCounter(counter + 1)
    // setCounter(counter + 1)
    // setCounter(counter + 1)
    // ra két quả khác nhau
    setCounter(prev => prev + 1)
    setCounter(prev => prev + 1)
    // Lúc đầu thì counter  = 1 
    // Nếu truyền cho setCounter một callback với 1 đối số thì đối số đó sẽ là giá trị của counter trong lần đầu. 
    // Khi sử dụng nhiều hàm setCounter cùng một lúc thì nó sẽ nhận giá trị trước đó của hàm setCounter

  }
  console.log("code ")
  return (
    <div className="App" style = {{padding: '20px'}}>
        <h1>{counter}</h1>
        <button onClick = {handleIncrease}>Increase</button>
    </div>
  )
}
export default App;



