import {useState} from 'react'

function App(){
  const [info, setInfo] = useState({
      name: "Nguyễn Quang Vương",
      age: 18,
      address: 'VN, Hanoi'
  })
  const handleUpdate = () => {
      setInfo({
        ...info,
        bio: 'Yêu em hàng xóm '
      }
      )
  }
  return (
    <div className="App" style = {{padding: '100px'}}>
    <h1>{JSON.stringify(info)}</h1>
    <button onClick = {handleUpdate}>Update</button>
</div>
  )
}
export default App;
